javascript_examples.track_view:
fetch('/api/supplier/analytics/track-view', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    supplier_id: 49,
    location: 'Riyadh',
    customer_type: 'Large Organizations'
  })
})

javascript_examples.track_search:
fetch('/api/supplier/analytics/track-search', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    keyword: 'LED TV',
    search_type: 'supplier',
    location: 'Riyadh'
  })
})
